class s{static setCookie(e,o,i){document.cookie=`${e}=${o}; expires=${i.toUTCString()}; path=/`}static getCookie(e){const o=e+"=",n=decodeURIComponent(document.cookie).split(";");for(const c of n){let t=c;for(;t.startsWith(" ");)t=t.substring(1);if(t.startsWith(o))return t.substring(o.length,t.length)}return null}static getCookieJson(e){const o=s.getCookie(e);return o?JSON.parse(o):null}static removeCookie(e){document.cookie=`${e}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`}}export{s as C};
